#!/bin/bash

rg -oNIzP '((25[0-5]|(2[0-4]|1{0,1}[0-9]){0,1}[0-9])\.){3,3}(25[0-5]|(2[0-4]|1{0,1}[0-9]){0,1}[0-9])' /var/www/html > ips.txt
rg -oNIzP '((?:[a-z]{3,6}[0-9]?:)?//([a-zA-Z0-9\.\-]+(\:[a-zA-Z0-9\.&%\$\-]+)*@)*((25[0-5]|2[0-4][0-9]|[0-1]{1}[0-9]{2}|[1-9]{1}[0-9]{1}|[1-9])\.(25[0-5]|2[0-4][0-9]|[0-1]{1}[0-9]{2}|[1-9]{1}[0-9]{1}|[1-9]|0)\.(25[0-5]|2[0-4][0-9]|[0-1]{1}[0-9]{2}|[1-9]{1}[0-9]{1}|[1-9]|0)\.(25[0-5]|2[0-4][0-9]|[0-1]{1}[0-9]{2}|[1-9]{1}[0-9]{1}|[0-9])|localhost|([a-zA-Z0-9\-]+\.)*[a-zA-Z0-9\-]+(\.[a-z]{2,6}){1,2})(\:[0-9]+)*(\/($|[a-zA-Z0-9\.\,\?\\\+&%\$#\=~_\-]+))*)' /var/www/html > urls.txt
rg -oNIzP  '(?:[A-Za-z0-9+/]{4}){2,}(?:[A-Za-z0-9+/]{2}[AEIMQUYcgkosw048]==|[A-Za-z0-9+/][AQgw]==)'  /var/www/html > base64.txt
rg -oNIzP  '(?![^0-9a-f])[0-9a-f]{40}(?![^0-9a-f])'  /var/www/html > md5.txt
rg -oNIzP  '(?![^0-9a-f])[0-9a-f]{40}(?![^0-9a-f])'  /var/www/html > SHA1.txt

